class Solution(object):
	def maxArea0(self, height):
		"""
		:type height: List[int]
		:rtype: int
		THIS METHOD WILL LOOSE THE INDICES
		"""
		height.sort()
		# print("sorted", height)
		l = len(height)-1
		# m = [[i for i in height] for j in height]
		# print(m)
		mx = -99999999999999
		for i in range(l):
			for j in range(l, i, -1):
				# m[i][j] = max(m[i][j], height[i] * (j - i))
				mx = max(mx, height[i] * (j - i))
				# print(m[i][j], height[i], j, i)

		return mx


	def maxAreaTLE(self, height):
		"""
		:type height: List[int]
		:rtype: int
		NAIVE COMPARISION
		"""
		l = len(height)
		mx = -99999999999
		for i in range(l):
			for j in range(i+1, l):
				mx = max(mx, min(height[i], height[j]) * abs(j - i))
		return mx


	def maxArea(self, height):
		"""
		:type height: List[int]
		:rtype: int
		"""
		i, j, mx = 0, len(height)-1, 0
		while i < j:
			h = min(height[i], height[j])
			mx = max(mx, h * (j - i))
			while height[j] <= h and i<j: j -= 1
			while height[i] <= h and i<j: i += 1
		return mx

